// extraReducers = indicates are additional reducers apart from the reducers generated by create slice
import { createSlice } from "@reduxjs/toolkit";

const initialState = {
    isShowOTP: false,
    moblieNumber: '',
    index: '',
    isLoginPopupShow: false,
};

const landingPageSlice = createSlice({
  name: "landingPageSlice",
  initialState,
  reducers: {
    updateOTPValues: (state, action) => {
      state[action.payload.key] = action.payload.value;
    },
    updateMultipleOTPValues: (state, action) => {
      state = { ...state, ...action.payload };
      return state;
    },
    updateLoginValues: (state, action) => {
      state[action.payload.key] = action.payload.value;
    },
  },
});

export default landingPageSlice.reducer;
export const { updateOTPValues, updateMultipleOTPValues } =
landingPageSlice.actions;
